(11410, 607, '_element_1_image', 'field_63be46d2115a3'),
(11411, 607, 'element_2_title', 'Study chemistry sometimes can be a MAZE'),
(11412, 607, '_element_2_title', 'field_63be476567575'),
(11413, 607, 'element_2_caption', 'Dr Aw simplify all chapters of the syllabus into cookie, bite size. With crystal clear explanations, research proven teaching methods, top-notched teaching materials, learning Chemistry had never been easier'),
(11414, 607, '_element_2_caption', 'field_63be477d67576'),
